body.script:
	feather.replace()
    var prevscroll = 0
    var byId = (id) => document.getElementById(id);
    var gtCl = (id) => byId(id).classList;
    var shareurl = () => navigator.share({title: 'MDN',text: 'Learn web development on MDN!',url: 'https://developer.mozilla.org'})
    var showsearch = () => {
        gtCl("topsearch").add("nomargin");
        hidemenu();
        document.getElementsByName("q")[0].focus()
    }
    var hidesearch = () => {
        gtCl("topsearch").remove("nomargin");
        document.getElementsByName("q")[0].blur()
    }
    var showmenu = () => {
        gtCl("sidebar-main").add("nomargin");
        hidesearch()
    }
    var hidemenu = () => gtCl("sidebar-main").remove("nomargin");
    var abcheck = (up) => up ? gtCl("appbar").remove("nobar") : (gtCl("appbar").add("nobar") || hidesearch());

    function logic(w) {
        let st = byId("scrollcontainer").scrollTop;
        let up = st < prevscroll;
        abcheck(up);
        prevscroll= st
    }
    var totop = () => byId("scrollcontainer")?.scrollTo(0, 1);
    document.addEventListener("DOMContentLoaded", function() {
        byId("scrollcontainer")?.addEventListener('scroll', logic)
    });
